import React , {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

//Global variable for RESTAPI URL
const url = "http://localhost:3001/Users" //array type of object



class Restapi extends Component {
    constructor(props){
        super(props)

        this.state = 
        {
            userData:[] //empty Array

        }
    }



    render(){
        return(<>
            <div className="container p-5">

            <h3 class="text-center"> FETCH RESTAPI DATA USING LOCAL URL/CLIENT</h3>

            <table className="table table-dark table-bordered">

                <thead className="table table-light table-bordered">
                    <tr>
                        <th>ID</th>
                        <th>NAME</th>
                        <th>EMAIL</th>
                        <th>PHONE</th>
                        <th colSpan="3" className="text-center" >ACTION</th>
                    </tr>
                </thead>

                <tbody>
                        {
                        
                        this.state.userData.map( (res,i)=> {
                        return(<tr key={i}>

                                    <td>{res.id}</td>
                                    <td>{res.name}</td>
                                    <td>{res.email}</td>
                                    <td>{res.phone}</td>
                                    <td>
                                        <Link to={`/restapi/view/${res.id}`} className="btn btn-primary">View</Link>
                                    </td>
                                    <td>
                                        <button className="btn btn-warning">Edit</button>
                                    </td>
                                    <td>
                                        <button className="btn btn-danger">Delete</button>
                                    </td>

                            </tr>)
                            
                        } )
                    }

                </tbody>


            </table>

                
        </div>
    </>)
}
//RESTAPI_CALLS
componentDidMount()
{
    axios.get(url).then((result) => this.setState({ userData:result.data }));
}    

}

export default Restapi
