import React, { Component } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

//global VARIABLE FOR restapi_url
var idno;


export default class view extends Component
{
    
    constructor(props) {
        super(props)

        this.state = {} //empty
    }

    render(){
        idno = this.props.match.params.idno
        console.log(idno)

        var {id, name, email, phone } = this.state

        return(<>
            <div className="container p-5">
                 <h2>EDIT COMPONENT PAGE</h2>
                 
                 <form>
                        <div class="mb-3">
                            <label class="form-label">Id</label>
                            <input type="number" class="form-control" value={id}/>
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Name</label>
                            <input type="text" class="form-control" value={name}/>
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Email</label>
                            <input type="email" class="form-control" value={email}/>
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Contact Details</label>
                            <input type="number" class="form-control" value={phone}/>
                        </div>
                        <div class="mb-3">
                 <Link className="btn btn-success" to="/restapi">GOTO BACK</Link>
                            
                        </div>
                 </form>

                 
                 <Link className="btn btn-success" to="/restapi">GOTO BACK</Link>
            
            </div>
        </>)
    }

    //Restapi_calls
    componentDidMount()
     {
         const url= `http://localhost:3001/Users/${idno}`
         axios.get(url).then((result) => this.setState( result.data)); 
     }
 
}
