import React, { Component } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";

//global VARIABLE FOR restapi_url
var idno;


export default class view extends Component
{
    
    constructor(props) {
        super(props)

        this.state = {
            uname: '',
            email: '',
            phone: '',
            txtConformation:''
        } 
    }

    //Name_input_textbox
    unameHandling = (e) =>{
        this.setState( { uname: e.target.value })
    }

    //Email_input_textbox
    emailHandling = (e) =>{
        this.setState( { email: e.target.value })
    }

    //Phone_input_textbox
    phoneHandling = (e) =>{
        this.setState( { phone: e.target.value })
    }



    submitHandler = (e) =>
    {
        var FormData =
        {
           'name':this.state.uname,
           'email':this.state.email,
           'phone':this.state.phone

        }
        
        axios.put(url, FormData)
        .then(()=>
             this.props.history.push('/restapi') )
    }

    render(){
        idno = this.props.match.params.idno
        console.log(idno)

         //Destructing...
        var {id, uname, email, phone, txtConformation } = this.state

        return(<>
            <div className="container p-5">
                 <h4 className="text-center">EDIT COMPONENT PAGE</h4>
                 
                 <form  onSubmit={this.submitHandler.bind(this)}>
                       
                        <div class="mb-3">
                            <label class="form-label">Name</label>
                            <input type="text" class="form-control" 
                            value={uname}
                            onChange={this.unameHandling.bind(this)}/>
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Email</label>
                            <input type="email" class="form-control" 
                            value={email}
                            onChange={this.emailHandling.bind(this)}/>
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Contact Details</label>
                            <input type="number" class="form-control" 
                            value={phone}
                            onChange={this.phoneHandling.bind(this)}/>
                        </div>
                        <div class="mb-3">
                             <Link className="btn btn-success mx-2" to="/restapi" >GOTO BACK</Link>
                             <button type="submit" class="btn btn-warning text-white" value="update">UPDATE</button>
                        </div>
                 </form>
            
            </div>
        </>)
    }

    //Restapi_calls
    componentDidMount()
     {
         const url= `http://localhost:3001/Users/${idno}`
         axios.get(url).then((result) => this.setState( result.data)); 
     }
 
}
