import {React,Component} from 'react';
import {connect} from 'react-redux'
import empAction from  './../../Actions/empActions'
import userAction from  './../../Actions/userActions'
import studentsAction from  './../../Actions/studentsAction'
import { bindActionCreators } from 'redux';
class ReactReduxConnection extends Component{
    constructor(props){
        super(props);
        this.state={}
    }
    componentDidMount()
    {
        this.props.empAction();     
        this.props.userAction();
        this.props.studentsAction();

    }
    render(){
        return(<>
         <div className='container p-5'>

         <h2 className='text-center'> ReactReduxConnection Example </h2>
         </div>
         
            ### synchronous data from redux actions
        {
          this.props.empdata.map((res,i)  =>
          {
              return(<ul key={i}>
               <li>{res.id}</li>
               <li>{res.name}</li>
               <li>{res.email}</li>

              </ul>)
          })

        }
         ### Asynchronous data[reatapi] from redux actions
        {
          this.props.userdata.map((res,i)  =>
          {
              return(<ul key={i}>
               <li>{res.id}</li>
               <li>{res.name}</li>
               <li>{res.email}</li>

              </ul>)
          })

        }
         ### Asynchronous data-2[localapi] from redux actions
        {
          this.props.studentsdata.map((res,i)  =>
          {
              return(<ul key={i}>
               <li>{res.id}</li>
               <li>{res.name}</li>
               <li>{res.email}</li>

              </ul>)
          })

        }


        </>)
    }
}
const mapStateToProps =(state) =>{
    console.log(state)
return ({
    empdata:state.EmpReducer,
    userdata:state.userReducer,
    studentsdata:state.studentsReducer
})
}
 const mapDispatchToProps =(dispatch) =>{
    return  bindActionCreators({empAction,userAction,studentsAction},dispatch)
}
//export default ReactReduxConnection
export default connect(mapStateToProps,mapDispatchToProps)(ReactReduxConnection)
